import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders} from '@angular/common/http';
import { Observable } from 'rxjs';
import {map} from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ApiMarvelService {
  PUBLIC_KEY = '0a2afee85ec52df1f4876fbf810b127b';
  HASH = '10c05813bf02852c89c74bfd3d6368a4';
  URL_API = `https://gateway.marvel.com:443/v1/public/comics?ts=1&apikey=${this.PUBLIC_KEY}&hash=${this.HASH}`;

  constructor(
    private _http:HttpClient
  ) { }

  getAllComics():Observable<any>{
      return this._http.get<any>(this.URL_API).pipe(map((data:any)=>{
        console.log("response", data);
        data.results
      }))
  }
}
