import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, FormControl } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  loginForm!: FormGroup<any>;

  constructor(
    private readonly fb: FormBuilder
  ){ }

  ngOnInit():void {
    this.loginForm = this.initForm();
  }

  onSubmit():void{
    console.log("form => Enviado", this.loginForm)
  }

  initForm():any {
    return this.fb.group(
      {
        username: ['',[Validators.required, Validators.minLength(4), Validators.maxLength(15), Validators.pattern(/^[a-zA-z0-9\_\-]/)]],
        password: ['',[Validators.required, Validators.minLength(5), Validators.maxLength(15)], Validators.pattern(/^[a-zA-z0-9\_\-]/)]
      }
    )
  }
}
